import React, { JSX, useState } from "react";
import {
  Platform,
  StyleSheet,
  Text,
  TextInput,
  TextInputProps,
  TouchableOpacity,
  View,
  ViewStyle,
} from "react-native";
import CustomText from "./CustomText";

type CustomInputProps = TextInputProps & {
  label: string;
  error?: string;
  success?: boolean;
  loading?: boolean;
  containerStyle?: ViewStyle;
  required?: boolean;
  isPhoneInput?: boolean;
  prefix?: JSX.Element;
  suffix?: JSX.Element;
  isFocusable?: boolean;
};

const CustomInput = ({
  label,
  error,
  loading,
  containerStyle,
  secureTextEntry,
  required,
  isPhoneInput,
  prefix,
  suffix,
  onBlur,
  isFocusable = true,
  ...textInputProps
}: CustomInputProps) => {
  const [isSecure, setIsSecure] = useState(secureTextEntry);
  const [isFocused, setIsFocused] = useState(false);

  const handleBlur = (e: any) => {
    setIsFocused(false);
    if (onBlur) onBlur(e);
  };

  return (
    <View style={containerStyle}>
      <View style={styles.labelWrapper}>
        <Text style={styles.label}>{label}</Text>
        {required && (
          <View style={{ marginLeft: scale(4) }}>
            <AsteriskIcon />
          </View>
        )}
      </View>
      <View
        style={[
          styles.inputWrapper,
          {
