            </View>
          </View>
        )}
        {prefix}
        <TextInput
          {...textInputProps}
          secureTextEntry={isSecure}
          style={[styles.input, textInputProps.style]}
          onFocus={() => (isFocusable ? setIsFocused(true) : {})}
          onBlur={handleBlur}
        />
        {suffix}

        {secureTextEntry && (
          <TouchableOpacity
            style={styles.prefix}
            onPress={() => setIsSecure((prev) => !prev)}
