type CustomButtonProps = TouchableOpacityProps & {
  title: string;
  textStyles?: TextStyle;
  prefix?: JSX.Element;
  suffix?: JSX.Element;
  disabled?: boolean;
  loading?: boolean;
  backgroundColor?: string;
};

const CustomButton = ({
  title,
  style,
  textStyles,
  prefix,
  suffix,
  disabled = false,
  loading = false,
  backgroundColor = "black",
  ...buttonProps
}: CustomButtonProps) => {
  return (
    <TouchableOpacity
      style={[
        styles.button,
        style,
        {
          backgroundColor: backgroundColor,
          opacity: disabled ? 0.4 : 1,
        },
      ]}
      disabled={disabled || loading}
      {...buttonProps}
    >
      {prefix}
      {loading ? (
        <ActivityIndicator
          color={backgroundColor === "white" ? COLORS.primary : "white"}
