hooks/useApi.ts:
import apiService from "@/api/apiService";
import { useState } from "react";

const useApi = () => {
  const [loading, setLoading] = useState<boolean>(false);

  const fetchData = async (
    method: "get" | "post" | "put" | "delete",
    endpoint: string,
    payload?: any,
    headers: Record<string, string> = {}  
  ) => {
    setLoading(true);
    try {
      const response = await apiService[method](endpoint, payload, headers);
      return response;
    } catch (err: any) {
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return { loading, fetchData };
};

export default useApi;

hooks/useCachedResources.ts:
import * as Font from "expo-font";
import * as SplashScreen from "expo-splash-screen";
import { useEffect, useState } from "react";

import {
  KronaOneRegular,
  UrbanistBold,
  UrbanistMedium, UrbanistMediumItalic, UrbanistRegular, UrbanistSemiBold,
} from "../assets/fonts";

const useCachedResources = () => {
  const [isLoadingComplete, setLoadingComplete] = useState(false);

  // Load any resources or data that we need prior to rendering the app
  useEffect(() => {
    const loadResourcesAndDataAsync = async () => {
      try {
        // Keep the splash screen visible while we fetch resources
        SplashScreen.preventAutoHideAsync();

        // Load fonts
        await Font.loadAsync({
          "Urbanist-Bold": UrbanistBold,
          Urbanist: UrbanistRegular,
          "Urbanist-SemiBold": UrbanistSemiBold,
          "Urbanist-Medium": UrbanistMedium,
          "Urbanist-Medium-Italic": UrbanistMediumItalic,
          KronaOne: KronaOneRegular
        });
      } catch (e) {
        console.warn(e);
      } finally {
        setLoadingComplete(true);
        SplashScreen.hideAsync();
      }
    };

    loadResourcesAndDataAsync();
  }, []);

  return { isLoadingComplete };
};

export default useCachedResources;

stores/useAppStore.ts:
import { Storage } from '@/utility/asyncStorageHelper';
import { create } from 'zustand';

type User = {
  id: string;
  phone: string;
  firstName?: string;
  lastName?: string;
  gender?: string;
  role: string;
};


type DestinationLocation = {
  address: string;
  coords: { latitude: string; longitude: string };
};

type PickUpLocation = {
  address: string;
  coords: { latitude: string; longitude: string };
};



type AppState = {
  user: User | null;
  token: string | null;
  destinationLocation: DestinationLocation | null;
  pickupLocation: PickUpLocation | null;
  setDestinationLocation: (location: DestinationLocation) => void;
  setPickupLocation: (location: PickUpLocation) => void;

};

type AppActions = {
  setUser: (user: User | null, token?: string) => Promise<void>;
  loadFromStorage: () => Promise<void>;
  resetStore: () => Promise<void>;
};

type AppStore = AppState & AppActions;

export const useAppStore = create<AppStore>((set) => ({
  user: null,
  token: null,
  destinationLocation: null,
  pickupLocation: null,
  setDestinationLocation: (location) => set({ destinationLocation: location }),
  setPickupLocation: (location) => set({ pickupLocation: location }),

  setUser: async (user, token) => {
    try {
      if (user && token) {
        await Storage.set('user', JSON.stringify(user));
        await Storage.set('access_token', token);
        set({ user, token });
      } else {
        await Storage.remove('user');
        await Storage.remove('access_token');
        set({ user: null, token: null });
      }
    } catch (err) {
      console.error('Failed to set user', err);
    }
  },

  loadFromStorage: async () => {
    try {
      const user = await Storage.get<string>('user');
      const token = await Storage.get<string>('access_token');
      set({
        user: user ? JSON.parse(user) : null,
        token: token || null
      });
    } catch (err) {
      console.error('Failed to load from storage', err);
    }
  },

  resetStore: async () => {
    try {
      await Promise.all([
        Storage.remove('user'),
        Storage.remove('access_token')
      ]);
      set({ user: null, token: null });
    } catch (err) {
      console.error('Failed to reset store', err);
    }
  }
}));

